-- ----------------------------
-- Table structure for qarobot_error
-- ----------------------------
DROP TABLE IF EXISTS `qarobot_error`;
CREATE TABLE `qarobot_error` (
  `CREATEDATE` varchar(128) default NULL,
  `THREAD` varchar(128) default NULL,
  `GRADE` varchar(128) default NULL,
  `CLASS` varchar(128) default NULL,
  `MESSAGE` text,
  `PID` bigint(24) default NULL,
  `CURRENTSTATE` varchar(255) default NULL,
  `AUTOID` bigint(20) NOT NULL auto_increment,
  PRIMARY KEY  (`AUTOID`)
) ENGINE=InnoDB DEFAULT CHARSET=gbk;

