-- Table structure for qarobot_qalist
-- ----------------------------
DROP TABLE IF EXISTS `qarobot_qalist`;
CREATE TABLE `qarobot_qalist` (
  `id` int(11) NOT NULL auto_increment COMMENT '问题id 自增',
  `question` varchar(128) NOT NULL default '' COMMENT '问题文本描述',
  `answer` text NOT NULL COMMENT '答案文本描述',
  `keyword` varchar(128) default NULL COMMENT '关键字 空格分隔',
  `remark` varchar(128) default NULL COMMENT '说明',
  `createtime` varchar(14) default NULL,
  `updatetime` varchar(14) default NULL,
  `isvalid` int(11) default '1' COMMENT '1-有效，0-无效',
  `sysid` int(11) default '0',
  `client` varchar(64) default NULL,
  PRIMARY KEY  (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

